import React, {useState, useEffect} from 'react'
import articleContent from './article-content'
import Articles from '../components/Articles'
import NotFound from './NotFound'
import CommentsList from '../components/CommentsList'

const Article = ({match}) => {
    const name =match.params.name;
    const article = articleContent.find((article) => article.name === name);

    const [articleInfo, setArticleInfo] = useState({  comments: [] });

    useEffect(() => {
        const fetchData = async () => {
            const result = await fetch(`/api/articles${name}`);
            const body =  await result.json();
            setArticleInfo(body);
        }
        fetchData();
    }, [name]);

    if(!article) return <NotFound />
    const otherArticles = articleContent.filter((article) => article.name !== name);
    return (
        <>
            <h1 className="sm:text-4xl text-2xl font-bold mt-6 mb-6 text-gray-900">{article.title}</h1>
            {article.content.map((paragraph, index) => (
                <p className="mx-auto leading-relaxed text-base mb-4">{paragraph}</p>
            ))}
            <CommentsList comments={articleInfo.comments} />
            <h1 className="sm:text-2x text-xl font-bold mt-4 mb-4 text-gray-900">Other Articles</h1>
            <div className="flex flex-wrap -m-4">
                <Articles articles={otherArticles} />
            </div>
        </>
    )
}

export default Article
