import React, { Component }from "react";
import Reacr from "react";
import './App.css';

import BlogCard from "./BlogCard";

class App extends Component {
  state = {
    showBlogs: true
  }
  blogArr = [
    {
      id: 1,
      title: 'Blog Title 1',
      description: 'Yowwwwwwwwwww',
      likeCount: 0
    },
    {
      id: 2,
      title: 'Blog Title 2',
      description: 'Yowwwwwwwwwww',
      likeCount: 0
    },
    {
      id: 3,
      title: 'Blog Title 3',
      description: 'Yowwwwwwwwwww',
      likeCount: 0
    }
  ]

  blogCards = this.blogArr.map((item, pos) => {

    return (
      <BlogCard key={pos} title={item.title} description=
      {item.description} likeCount={item.likeCount} id={item.id} onLikeBtnClick={() =>
        this.onLikeBtnClick(pos)}/>
      // <div className="Blogcard" key={item.id}>
      //   <h3>{item.title}</h3>
      //   <p>{item.description}</p>
      //   </div>
    )
  })

  onHideBtnClick = () => {
    let updatedState = !this.state.showBlogs;
    this.setState({showBlogs: updatedState});

    console.log(this.showBlogs)
  }

  render(){
    console.log('Render Called');
    return(
      <div className="App">
    <button onClick={this.onHideBtnClick}>{this.state.showBlogs ? 'Hide List' : 'Show list'}</button>
    <br></br>
    {
      this.state.showBlogs ? this.blogCards : null}
    </div>
    );
  }
}

export default App;
