import React, { Component }from "react";
import Reacr from "react";
import './App.css';

import BlogCard from "./BlogCard";

class App extends Component {

  blogArr = [
    {
      id: 1,
      title: 'Blog Title 1',
      description: 'Yowwwwwwwwwww'
    },
    {
      id: 2,
      title: 'Blog Title 2',
      description: 'Yowwwwwwwwwww'
    },
    {
      id: 3,
      title: 'Blog Title 3',
      description: 'Yowwwwwwwwwww'
    }
  ]
  showBlogs = true;
  blogCards = this.blogArr.map((item, pos) => {

    return (
      <BlogCard key={pos} title={item.title} description={item.description} id={item.id}/>
      // <div className="Blogcard" key={item.id}>
      //   <h3>{item.title}</h3>
      //   <p>{item.description}</p>
      //   </div>
    )
  })

  onHideBtnClick = () => {
    alert ('Button Clicked');
  }

  render(){
    return(
      <div className="App">
    <button onClick={this.onHideBtnClick}>Hide List</button>
    <br></br>
    {
      this.showBlogs ? this.blogCards : null}
    </div>
    );
  }
}

export default App;
